mapper.select:
SELECT * FROM ( SELECT A.USERNO,A.USERNAME,A.USERID ,A.EMAIL,A.USERDUTY ,CASE WHEN A.ISDELETE = 0 THEN '사용' WHEN A.ISDELETE = 1 THEN '미사용' END AS ISDELETE_ ,CASE WHEN DATE_FORMAT(A.REGDATE,'%Y-%m-%d') = '1900-01-01' THEN '' ELSE DATE_FORMAT(A.REGDATE,'%Y-%m-%d') END AS REGDATE ,A.MOBILE1,A.MOBILE2,A.MOBILE3 ,CASE WHEN DATE_FORMAT(A.ENTERDATE,'%Y-%m-%d') = '1900-01-01' THEN '' ELSE DATE_FORMAT(A.ENTERDATE,'%Y-%m-%d') END AS ENTERDATE , CONCAT(A.MOBILE1, A.MOBILE2, A.MOBILE3) AS MOBILE ,(SELECT COUNT(*) FROM CTI510010 WHERE CALLSTATUS = 1 AND CALLUSER = A.USERNO) AS CTICOUNT ,@RNUM:=@RNUM + 1 AS ROWNUM FROM me800010 A, (SELECT @RNUM := 0) R WHERE A.SITEID = #{siteid} AND A.ISDELETE = 0 AND A.MASTERYN != 1
AND A.USERNAME LIKE CONCAT('%',#{username},'%')
AND A.MOBILE LIKE CONCAT('%',#{mobile},'%')
AND A.USERID LIKE CONCAT('%',#{userid},'%')
AND A.ISDELETE = #{isdelete}
AND DATE_FORMAT(REGDATE,'%Y-%m-%d') >= #{strdate}
AND DATE_FORMAT(REGDATE,'%Y-%m-%d') <= #{enddate}
AND ROLE = #{role}
AND USERNO = #{userNo}
ORDER BY A.USERNO DESC ) X
WHERE X.ROWNUM Between #{startRowNum} and #{endRowNum}

SELECT count(*)
		FROM me800010
		WHERE SITEID = #{siteid}
		AND ISDELETE = 0
		AND MASTERYN != 1

			AND USERNAME LIKE CONCAT('%',#{username},'%')


			AND MOBILE LIKE CONCAT('%',#{mobile},'%')


			AND USERID LIKE CONCAT('%',#{userid},'%')


			AND ISDELETE = #{isdelete}


			AND REGDATE >=#{strdate}


			AND REGDATE <=#{enddate}


			AND USERNO = #{userNo}

