		,MOBILE2 = #{mobile2}
		,MOBILE3 = #{mobile3}
		,TELNO1 = #{telno1}
		,TELNO2 = #{telno2}
		,TELNO3 = #{telno3}

		,CHKAUTH = #{chkauth}

		,EDTDATE = NOW()
		,EDTUSER = #{edtuser}
	WHERE USERNO = #{userno}


UPDATE me810011
	SET ISDELETE = 1
	WHERE USERNO = #{userno}
	AND SITEID = #{siteid}

mapper.insert:
			INSERT INTO me810011   (SITEID,USERNO,MENUNO,REGDATE,REGUSER,EDTDATE,EDTUSER,ISDELETE)
