mapper.select:
SELECT * FROM ( SELECT A.CAMPNO , A.CAMPNAME , A.CAMPTYPE , CASE WHEN A.CAMPTYPE = 10 THEN '세미나' WHEN A.CAMPTYPE = 20 THEN '뉴스레터' WHEN A.CAMPTYPE = 30 THEN '테스트' END AS CAMPTYPE_ , A.CAMPSTEP , CASE WHEN A.CAMPSTEP = 0 THEN '생성' WHEN A.CAMPSTEP = 1 THEN '추출' WHEN A.CAMPSTEP = 2 THEN '발송' WHEN A.CAMPSTEP = 3 THEN '종료' END AS CAMPSTEP_ , A.STARTDATE,A.ENDDATE , (SELECT USERNAME FROM me800010 WHERE USERNO = A.OWNER) AS OWNER_ , A.CAMPDESC , CONCAT(DATE_FORMAT(A.STARTDATE,'%Y-%m-%d') , ' ~ ' , DATE_FORMAT(A.ENDDATE,'%Y-%m-%d')) AS CAMPDATE_ ,IFNULL(B.CNT,0) AS CNT ,IFNULL(C.CNT,0) AS CLICKCNT , CONCAT(IFNULL(C.CNT,0) , ' / ' ,IFNULL(B.CNT,0)) AS CLICKTOTAL , CONCAT(IFNULL(D.CNT,0) , ' / ' ,IFNULL(B.CNT,0)) AS SENDTOTAL , CASE WHEN E.SENDFORM = 1 THEN 'EMAIL' WHEN E.SENDFORM = 2 THEN 'SMS/LMS' WHEN E.SENDFORM = 4 THEN 'MMS' END AS CAMPFORM_ ,@RNUM:=@RNUM + 1 AS ROWNUM FROM cp400010 A LEFT OUTER JOIN (SELECT CAMPNO,COUNT(EMAILLOGID) AS CNT FROM sys980010 GROUP BY CAMPNO) B ON B.CAMPNO = A.CAMPNO LEFT OUTER JOIN (SELECT CAMPNO,COUNT(EMAILLOGID) AS CNT FROM sys980010 WHERE CLICKYN = 0 GROUP BY CAMPNO) C ON C.CAMPNO = A.CAMPNO LEFT OUTER JOIN (SELECT CAMPNO,COUNT(EMAILLOGID) AS CNT FROM sys980010 WHERE RLTCODE = 20 GROUP BY CAMPNO) D ON C.CAMPNO = A.CAMPNO LEFT OUTER JOIN cp400030 E ON E.CAMPNO = A.CAMPNO ,(SELECT @RNUM := 0) R WHERE A.SITEID = #{siteid} AND A.ISDELETE = 0
AND A.CAMPNAME LIKE CONCAT('%' , #{campname} , '%')
AND A.CAMPSTEP = #{campstep}
AND A.CAMPTYPE = #{camptype}
AND A.STARTDATE >= #{startdate}
AND A.ENDDATE <= #{enddate}
AND A.OWNER = #{owner}
AND E.SENDFORM = #{sendform}
ORDER BY A.CAMPNO DESC ) X
WHERE X.ROWNUM Between #{startRowNum} and #{endRowNum}

SELECT count(*)
		FROM cp400010 A
		WHERE A.SITEID = #{siteid}
		AND A.ISDELETE = 0

			AND  A.CAMPNAME LIKE CONCAT('%' , #{campname} , '%')


			AND  A.CAMPSTEP = #{campstep}


			AND  A.CAMPTYPE = #{camptype}


			AND  A.STARTDATE >= #{startdate}


			AND  A.ENDDATE <= #{enddate}


			AND  A.OWNER = #{owner}

