SELECT * FROM (SELECT A.CUSTNO ,A.CUSTNAME ,A.MOBILENO ,A.EMAIL ,CASE WHEN B.INFOAGREE = 0 THEN '동의' WHEN B.INFOAGREE = 1 THEN '미동의' END AS INFOAGREE_ ,CASE WHEN B.CUSTGUBUN = 1 THEN '회원' WHEN B.CUSTGUBUN = 2 THEN '비회원' END AS CUSTGUBUN_ ,CASE WHEN B.CUSTGRADE = 1 THEN '일반' WHEN B.CUSTGRADE = 2 THEN 'VIP' WHEN B.CUSTGRADE = 3 THEN 'VVIP' END AS CUSTGRADE_ ,CONCAT(B.HOMADDR1 ,' ', B.HOMADDR2 , B.HOMADDR3) AS ADDR_ ,@RNUM:=@RNUM + 1 AS ROWNUM FROM cp400021 A INNER JOIN cu100010 B ON B.CUSTNO = A.CUSTNO ,(SELECT @RNUM := 0) R WHERE A.SITEID = #{siteid} AND A.CAMPNO = #{campno} AND B.ISDELETE = 0
AND A.CUSTNAME LIKE CONCAT('%', #{custname} ,'%')
) X WHERE X.ROWNUM Between #{startRowNum} and #{endRowNum}

