mapper.select:
SELECT 
	COUNT(*)
FROM sa200010  
WHERE 
	SITEID = #{siteid}
	AND ISDELETE = 0

	AND CLINAME LIKE CONCAT('%',#{cliname},'%')


	AND CALLNAME LIKE CONCAT('%',#{callname},'%')


	AND OWNER = #{owner}


	AND BSTYPE LIKE CONCAT('%',#{bstype},'%')


	AND IMPORTANCE = #{importance}


	AND FRIENDLY = #{friendly}

SELECT ROWNUM ,CLINO, CLINAME, CALLNAME, ADDR,BSTYPE,MEMO ,IMPORTANCE, FRIENDLY ,OWNER, USERNAME FROM ( SELECT @RNUM:=@RNUM + 1 AS ROWNUM ,CLINO, CLINAME, CALLNAME ,CONCAT(CLIADDR1,' ',CLIADDR2,' ',CLIADDR3) AS ADDR ,BSTYPE,MEMO ,IMPORTANCE, FRIENDLY ,A.OWNER, B.USERNAME FROM sa200010 A LEFT OUTER JOIN me800010 B ON A.OWNER = B.USERNO ,(SELECT @RNUM := 0) R WHERE A.SITEID = #{siteid} AND A.ISDELETE = 0
AND A.CLINAME LIKE CONCAT('%',#{cliname},'%')
AND A.CALLNAME LIKE CONCAT('%',#{callname},'%')
AND A.OWNER = #{owner}
AND A.BSTYPE LIKE CONCAT('%',#{bstype},'%')
AND IMPORTANCE = #{importance}
AND FRIENDLY = #{friendly}
ORDER BY CLINAME ) T
WHERE ROWNUM between #{startRowNum} and #{endRowNum}

