mapper.select:
SELECT A.CLICUSTNO,A.CLINO ,A.KEYMAN, A.ROLENAME, A.CHKSUPPORT, A.MEMO ,A.CUSTNO, B.CUSTNAME
FROM sa280010 A INNER JOIN CU100010 B ON A.CUSTNO = B.CUSTNO
WHERE A.SITEID = #{siteid} AND A.CLICUSTNO = #{clicustno}

mapper.update:

