mapper.select:
SELECT * FROM ( SELECT A.CODENO,A.CODEGRP,A.CODENAME,A.CODEVAL ,DATE_FORMAT(A.REGDATE,'%Y-%m-%d') AS REGDATE
,CASE WHEN A.ISDELETE = 0 THEN '사용' WHEN A.ISDELETE = 1 THEN '미사용' END AS ISDELETE_ ,A.ISDELETE ,B.CODENAME AS UPPERCODENAME ,A.UPPERCODEGRP ,@RNUM:=@RNUM + 1 AS ROWNUM FROM sys990020 A LEFT OUTER JOIN sys990020 B ON B.CODENO = A.UPPERCODEGRP ,(SELECT @RNUM := 0) R WHERE A.ISDELETE = 0
AND A.SITEID = 1
AND A.SITEID = #{siteid}
AND A.CODENAME LIKE CONCAT('%',#{codename},'%')
AND A.CODEGRP LIKE CONCAT('%',#{codegrp},'%')
) X
WHERE X.ROWNUM Between #{startRowNum} and #{endRowNum}

mapper.update:

UPDATE sys990020
SET 
	CODEGRP = #{codegrp}
	,CODENAME = #{codename}
	,CODEVAL = #{codeval}
	,UPPERCODEGRP = #{uppercodegrp}
	,ISDELETE = #{isdelete}
	,EDTDATE = now()
	,EDTUSER = #{edtuser}
WHERE SITEID = #{siteid}
AND CODENO = #{codeno}

mapper.insert:

INSERT INTO sys990020
