SELECT LAST_INSERT_ID()

mapper.update:

UPDATE sys990020
SET ISDELETE = 1
,EDTDATE = now()
,EDTUSER = #{edtuser}
WHERE SITEID = #{siteid}
AND CODENO = #{codeno}

mapper.select:
