mapper.select:
SELECT COUNT(*) FROM me800010 WHERE SITEID = #{siteid} AND ISDELETE = 0
AND USERNAME like CONCAT('%',#{username},'%')
AND CHKAUTH = #{chkauth}

SELECT * 
FROM  
	(
	SELECT 

	@RNUM:=@RNUM + 1 AS ROWNUM
	,USERNO, USERNAME, USERID, USERDUTY,CHKAUTH
	FROM me800010 ,(SELECT @RNUM := 0) R
	WHERE 
		SITEID= #{siteid}
		AND ISDELETE = 0

			AND USERNAME like CONCAT('%',#{username},'%')


			AND CHKAUTH = #{chkauth}

	ORDER BY USERNAME
	) T
WHERE 
	ROWNUM BETWEEN #{startRowNum} and #{endRowNum}

SELECT * FROM ( SELECT @RNUM:=@RNUM + 1 AS ROWNUM ,A.CLINO, A.CLINAME, A.CALLNAME, A.OWNER ,B.USERNAME FROM sa200010 A LEFT OUTER JOIN me800010 B ON A.OWNER = B.USERNO ,(SELECT @RNUM := 0) R WHERE A.SITEID= #{siteid} AND A.ISDELETE = 0
AND CLINAME like CONCAT('%',#{cliname},'%')
ORDER BY A.CLINAME ) T WHERE ROWNUM BETWEEN #{startRowNum} and #{endRowNum}

SELECT * FROM ( SELECT @RNUM:=@RNUM + 1 AS ROWNUM ,A.SALESNO, A.SALENAME, A.CLINO, A.OWNER ,B.USERNAME ,C.CLINAME FROM sa210010 A LEFT OUTER JOIN me800010 B ON A.OWNER = B.USERNO LEFT OUTER JOIN sa200010 C ON A.CLINO = C.CLINO ,(SELECT @RNUM := 0) R WHERE A.SITEID= #{siteid} AND A.ISDELETE = 0
AND SALENAME like CONCAT('%',#{salename},'%')
ORDER BY A.SALENAME ) T WHERE ROWNUM BETWEEN #{startRowNum} and #{endRowNum}

