mapper.update:

UPDATE ma900010
	SET ISDELETE = 1
	,EDTDATE = NOW()
	,EDTUSER = #{edtuser}
WHERE SITEID = #{siteid}

mapper.insert:
INSERT INTO ME810010	(SITEID,LICENSENO,BUYCNT,REGDATE,REGUSER,EDTDATE,EDTUSER)
				VALUES(#{siteid},#{licenseno},#{buycnt},NOW(),#{reguser},NOW(),#{edtuser})
ON DUPLICATE KEY UPDATE BUYCNT = #{buycnt},EDTDATE = NOW(),EDTUSER = #{edtuser}
