mapper.select:
SELECT * FROM ( SELECT A.CONTENTNO , A.FORMTYPE , A.MENUTYPE , CASE WHEN A.MENUTYPE = 1 THEN 'VOC' WHEN A.MENUTYPE = 2 THEN '캠페인' END MENUTYPE_ , CASE WHEN A.FORMTYPE = 1 THEN 'EMAIL' WHEN A.FORMTYPE = 2 THEN 'SMS/LMS' WHEN A.FORMTYPE = 3 THEN 'MMS' END FORMTYPE_ , A.TITLE, A.CONTENT, A.PURP , A.SITEID, A.ISDELETE , DATE_FORMAT(A.REGDATE,'%Y-%m-%d') AS REGDATE_ , (SELECT USERNAME FROM me800010 WHERE USERNO = A.REGUSER) AS REGUSER_ , DATE_FORMAT(A.EDTDATE,'%Y-%m-%d') AS EDTDATE_ , (SELECT USERNAME FROM me800010 WHERE USERNO = A.EDTUSER) AS EDTUSER_ , USECNT , @RNUM:=@RNUM + 1 AS ROWNUM FROM sys990050 A ,(SELECT @RNUM := 0) R WHERE A.SITEID = #{siteid} AND A.ISDELETE = 0
AND TITLE LIKE CONCAT('%',#{title},'%')
AND FORMTYPE = #{formtype}
AND MENUTYPE = #{menutype}
) X WHERE X.ROWNUM Between #{startRowNum} and #{endRowNum}

SELECT COUNT(CONTENTNO) 
FROM sys990050
WHERE SITEID = #{siteid}
AND ISDELETE = 0

	AND  TITLE LIKE CONCAT('%' , #{title} , '%')


	AND  FORMTYPE = #{formtype}


	AND MENUTYPE = #{menutype}

mapper.insert:
INSERT INTO sys990050(
	FORMTYPE
	,MENUTYPE
	,TITLE
	,CONTENT
	,PURP
	,SITEID
	,ISDELETE
	,REGDATE
	,REGUSER
	,EDTDATE
	,EDTUSER
)VALUES(
#{formtype}
,#{menutype}
,#{title}
,#{content}
,#{purp}
,#{siteid}
,0
,NOW()
,#{reguser}
,NOW()
,#{edtuser}
)

	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE sys990050
	SET FORMTYPE = #{formtype}
	,MENUTYPE = #{menutype}
	,TITLE = #{title}
	,CONTENT = #{content}
	,PURP = #{purp}
	,EDTDATE = NOW()
	,EDTUSER = #{edtuser}
WHERE CONTENTNO = #{contentno}
AND SITEID = #{siteid}

UPDATE sys990050
	SET EDTDATE = NOW()
	,EDTUSER= #{edtuser}
	,ISDELETE = 1
WHERE CONTENTNO = #{contentno}
AND SITEID = #{siteid}
