mapper.select:
SELECT A.FORMATNO AS NO ,A.FORMATNAME ,A.FORMATDESC ,A.SENDTYPE ,A.SITEID ,'/company/format/' AS URL ,(SELECT SUBSTRING_INDEX(LICENSENAME,'라이센스', 1) FROM MA900020 WHERE LICENSENO = A.USEMENU) AS USEMENU_ ,FN_GETCODE('FORMTYPE',A.SENDTYPE,#{siteid}) AS SENDTYPE_ FROM SYS990050 A WHERE SITEID = #{siteid} AND ISDELETE = 0
AND A.FORMATNAME like CONCAT('%',#{formatname},'%')
AND A.SENDTYPE = #{sendtype}
AND A.USEMENU = #{usemenu}

SELECT A.*
,FN_GETCODE('FORMTYPE',A.SENDTYPE,#{siteid}) AS SENDTYPE_
,(SELECT SUBSTRING_INDEX(LICENSENAME,'라이센스', 1) FROM MA900020 WHERE LICENSENO = A.USEMENU) AS USEMENU_
FROM SYS990050 A
WHERE A.SITEID = #{siteid}
AND A.FORMATNO = #{formatno}

mapper.update:
UPDATE SYS990050
SET FORMATNAME = #{formatname}
,FORMATDESC = #{formatdesc}
,SENDTYPE = #{sendtype}
,USEMENU = #{usemenu}
,KKOSERVICENO = #{kkoserviceno}
,KKOTEMPLETENO = #{kkotempleteno}
,EDTDATE = now()
,EDTUSER = #{edtuser}
WHERE SITEID = #{siteid}
AND FORMATNO = #{formatno}

UPDATE SYS990050
SET ISDELETE = 1
WHERE SITEID = #{siteid}
AND FORMATNO = #{formatno}
