mapper.select:
SELECT SERVICENO,SERVICENAME_,SERVICECHANNEL_,CUSTNAME_ ,CLINAME_,RECEPTIONDATE_,SERVICEOWNER_,OWNER_,SERVICESTEP_,SERVICETYPE_ ,IF(LENGTH(SERVICECODE_) < 4,REPLACE(SERVICECODE_,'/',''),SERVICECODE_) AS SERVICECODE_ ,RECFILENAME,RECDATE_,RECEXT,REQNO,URL FROM ( SELECT A.SERVICENO , A.SERVICENAME ,CASE WHEN CHAR_LENGTH(A.SERVICENAME) >= 20 THEN CONCAT( SUBSTRING(A.SERVICENAME,1,17) ,'...' ) ELSE IFNULL(A.SERVICENAME,'') END AS SERVICENAME_ , CONCAT(IF(A.SERVICECODE1=0,'',FN_GETCODE('SERVICECODE1',A.SERVICECODE1,#{siteid})),' / ',IF(A.SERVICECODE2=0,'',FN_GETCODEUPPER('SERVICECODE2',A.SERVICECODE2,#{siteid},FN_GETCODENO('SERVICECODE1',A.SERVICECODE1,#{siteid})))) AS SERVICECODE_ , FN_GETCODE('SERVICECHANNEL',A.SERVICECHANNEL,#{siteid}) AS SERVICECHANNEL_ , (SELECT CUSTNAME FROM cu100010 WHERE CUSTNO = A.CUSTNO) AS CUSTNAME_ , (SELECT CLINAME FROM sa200010 WHERE CLINO = A.CLINO) AS CLINAME_ , DATE_FORMAT(A.RECEPTIONDATE,'%Y-%m-%d') AS RECEPTIONDATE_ , (SELECT USERNAME FROM me800010 WHERE USERNO = A.SERVICEOWNER) AS SERVICEOWNER_ , IFNULL( (SELECT USERNAME FROM me800010 WHERE USERNO = A.OWNER) ,'') AS OWNER_ , FN_GETCODE('SERVICESTEP',A.SERVICESTEP,#{siteid}) AS SERVICESTEP_ , FN_GETCODE('SERVICETYPE',A.SERVICETYPE,#{siteid}) AS SERVICETYPE_ ,B.RECDATE, B.RECEXT, B.RECFILENAME, A.REQNO ,REPLACE(B.RECDATE,'-','') AS RECDATE_ ,CONCAT('/service/',SERVICENO) AS URL FROM sv300010 A LEFT OUTER JOIN cti510030 B ON A.REQNO = B.REQNO WHERE A.SITEID = #{siteid}
AND A.SERVICENAME LIKE CONCAT('%',#{servicename},'%')
) X

mapper.update:
UPDATE sv300010
	SET SERVICENAME = #{servicename}
	,SERVICEDESC = #{servicedesc}
	,SERVICEOWNER = #{serviceowner}
	,RECEPTIONDATE = #{receptiondate}
	,CUSTNO = #{custno}
	,CLINO = #{clino}
	,OWNER = #{owner}
	,FILESEARCHKEY = #{filesearchkey}
	,EDTDATE = NOW()
	,EDTUSER = #{edtuser}
	,SERVICETYPE = #{servicetype}
	,SERVICECODE1 = #{servicecode1}
	,SERVICECODE2 = #{servicecode2}
	,SERVICECHANNEL = #{servicechannel}
WHERE SERVICENO = #{serviceno}
AND SITEID = #{siteid}

Update sv300030
	SET VISITDATE = #{visitdate}
		,VISITTIME = #{visittime}
		,REWARDTYPE = #{rewardtype}
		,CAUSECODE = #{causecode}
		,DELAYTYPE = #{delaytype}
		,DELAYDESC = #{delaydesc}
		,REWARDDESC = #{rewarddesc}
		,FILESEARCHKEY = #{filesearchkey}
		,EDTDATE = NOW()
		,EDTUSER = #{edtuser}
	WHERE SITEID = #{siteid}
	AND SERVICENO = #{serviceno}
	AND REWARDNO = #{rewardno}

UPDATE sv300040
	SET RACTDATE = #{ractdate}
		,SENDYN = #{sendyn}
		,RACTDESC = #{ractdes}
		,FILESEARCHKEY = #{filesearchkey}
		,EDTDATE = NOW()
		,EDTUSER = #{edtuser}
		,RACTCODE = #{ractcode}
WHERE SITEID = #{siteid}
AND SERVICENO = #{serviceno}
AND RACTNO = #{ractno}

UPDATE sv300010
	SET SERVICESTEP = #{servicestep}
WHERE SERVICENO = #{serviceno}
AND SITEID = #{siteid}
