mapper.select:
SELECT A.SITENAME,A.SITEID as NO,A.PRSDNAME,A.BSTYPE ,A.BSNO1,A.BSNO2,A.BSNO3 ,A.MOBILE1,A.MOBILE2,A.MOBILE3 ,IF(DATE_FORMAT(A.FREGDATE,'%Y-%m-%d')='1900-01-01','',DATE_FORMAT(A.FREGDATE,'%Y-%m-%d')) AS FREGDATE_ ,FN_GETCODE('COMPANYSIZE',A.SITESIZE,1) AS SITESIZE_ ,FN_GETCODE('ISDELETE',A.ISDELETE,1) AS ISDELETE_ ,(SELECT SUM(BUYCNT) AS BUYCNT FROM me810010 WHERE SITEID = A.SITEID) AS BUYCNT ,CONCAT('/common/site/') AS URL ,A.BSNO,A.INCNO,A.TELNO,A.FAXTEL,A.MOBILE,DATE_FORMAT(A.REGDATE,'%Y-%m-%d') AS REGDATE FROM MA900010 A WHERE A.ISDELETE = 0
AND IFNULL(A.FREGDATE,'1900-01-01')>= #{regdatefr}
AND IFNULL(A.FREGDATE,'1900-01-01') <= #{regdateto}
AND A.SITENAME LIKE CONCAT('%',#{sitename},'%')
AND A.SITESIZE = #{sitesize}
AND A.PRSDNAME LIKE CONCAT('%',#{prsdname},'%')
AND A.ISDELETE = #{isdelete}

SELECT CTIID, CTIPASS, CTITELNO
FROM me800010
WHERE SITEID = #{siteid}
	AND USERNO = #{userno}

mapper.insert:

	SELECT LAST_INSERT_ID()

INSERT INTO ma900010
(
SITENAME,BSNO1,BSNO2,BSNO3,INCNO1,INCNO2
,ADDR1,ADDR2,ADDR3,FREGDATE,PRSDNAME
,MOBILE1,MOBILE2,MOBILE3,FAXTEL1,FAXTEL2,FAXTEL3
,TELNO1,TELNO2,TELNO3,EMAIL,SITESIZE
,OWNER,COTYPE,BSCOND,BSTYPE,ISDELETE
,SITEMEMO,REGDATE,REGUSER,EDTDATE,EDTUSER,SITELOGO,BSNO,INCNO,FAXTEL,TELNO,MOBILE
)VALUES(
#{sitename},#{bsno1},#{bsno2},#{bsno3},#{incno1},#{incno2}
,#{addr1},#{addr2},#{addr3},now(),#{prsdname}
,#{mobile1},#{mobile2},#{mobile3},#{faxtel1},#{faxtel2},#{faxtel3}
,#{telno1},#{telno2},#{telno3},#{email},#{sitesize}
,#{owner},#{cotype},#{bscond},#{bstype},#{isdelete}
,#{sitememo},NOW(),#{reguser},NOW(),#{edtuser},#{sitelogo},#{bsno},#{incno},#{faxtel},#{telno},#{mobile}
)

