mapper.select:
SELECT CODEGRP, CODENAME, CODEVAL
FROM sys990020
WHERE
	CODEGRP = #{codegrp}

	AND SITEID = #{siteid}

SELECT CODENO FROM SYS990020
WHERE SITEID = #{siteid}
AND CODEGRP = #{codegrp}
AND CODEVAL = #{codeval}

SELECT * FROM sys990020
WHERE SITEID = #{siteid}
AND UPPERCODEGRP = #{uppercodegrp}

SELECT A.CODENO AS NO,A.CODEGRP,A.CODENAME,A.CODEVAL ,DATE_FORMAT(A.REGDATE,'%Y-%m-%d') AS REGDATE ,FN_GETCODE('ISDELETE',A.ISDELETE,1) AS ISDELETE_ ,A.ISDELETE ,IFNULL(B.CODENAME,'') AS UPPERCODENAME ,A.UPPERCODEGRP ,(SELECT LICENSENAME FROM MA900020 WHERE LICENSENO = A.USINGMENU) AS USINGMENU FROM sys990020 A LEFT OUTER JOIN sys990020 B ON B.CODENO = A.UPPERCODEGRP WHERE A.ISDELETE = 0
AND A.SITEID = #{siteid}
AND A.CODENAME LIKE CONCAT('%',#{codename},'%')
AND A.CODEGRP LIKE CONCAT('%',#{codegrp},'%')
AND A.CODEVAL LIKE CONCAT('%',#{codeval},'%')
AND A.USINGMENU = #{usingmenu}

mapper.update:
UPDATE sys990020
SET
	CODEGRP = #{codegrp}
	,CODENAME = #{codename}
	,CODEVAL = #{codeval}
	,UPPERCODEGRP = #{uppercodegrp}
	,USINGMENU = #{usingmenu}
	,EDTDATE = now()
	,EDTUSER = #{edtuser}
WHERE SITEID = #{siteid}
AND CODENO = #{codeno}

mapper.insert:

SELECT LAST_INSERT_ID()

INSERT INTO sys990020
(
CODEGRP,CODENAME,CODEVAL,UPPERCODEGRP
,SITEID,ISDELETE,REGDATE,REGUSER,USINGMENU,COMMONFLAG
,EDTDATE,EDTUSER
)VALUES(
#{codegrp},#{codename},#{codeval},#{uppercodegrp}
,#{siteid},1,now(),#{reguser},#{usingmenu},1
,now(),#{edtuser}
)

mapper.update:
UPDATE sys990020
SET ISDELETE = 1
,EDTDATE = now()
,EDTUSER = #{edtuser}
WHERE SITEID = #{siteid}
AND CODENO = #{codeno}
