mapper.select:
SELECT CU.CUSTNO, CU.CUSTNAME, CU.DEPTNAME, CU.DUTY , CASE WHEN CHAR_LENGTH( CONCAT(IFNULL(CU.MOBILE1,''),IFNULL(CU.MOBILE2,''),IFNULL(CU.MOBILE3,'')) ) > 8 THEN CONCAT( IFNULL(CU.MOBILE1,''),'-',IFNULL(CU.MOBILE2,''),'-',IFNULL(CU.MOBILE3,'') ) ELSE CONCAT( IFNULL(CU.MOBILE1,''),IFNULL(CU.MOBILE2,''),IFNULL(CU.MOBILE3,'') ) END AS MOBILE_ ,CU.MOBILE1,CU.MOBILE2,CU.MOBILE3 ,CU.HOMTEL1,CU.HOMTEL2,CU.HOMTEL3 ,CU.HOMADDR1,CU.HOMADDR2,CU.HOMADDR3 , CU.EMAIL, CU.SEX , CU.OWNER, B.USERNAME AS OWNER_ , IFNULL((SELECT CODENAME FROM SYS990020 WHERE CODEVAL = CU.CUSTGUBUN AND CODEGRP = 'CUSTGUBUN'and SITEID = #{siteid} ),'' ) as CUSTGUBUN , IFNULL((SELECT CODENAME FROM SYS990020 WHERE CODEVAL = CU.CUSTGRADE AND CODEGRP = 'CUSTGRADE'and SITEID = #{siteid} ),'' ) as CUSTGRADE , IFNULL((SELECT CODENAME FROM SYS990020 WHERE CODEVAL = CU.INFOAGREE AND CODEGRP = 'INFOAGREE'and SITEID = #{siteid} ),'' ) as INFOAGREE , DATE_FORMAT(CU.REGDATE,'%Y-%m-%d') AS REGDATE , CU.CLINO, C.CLINAME FROM cu100010 CU LEFT OUTER JOIN me800010 AS B ON CU.OWNER = B.USERNO LEFT OUTER JOIN sa200010 AS C ON CU.CLINO = C.CLINO WHERE CU.SITEID = #{siteid} AND IFNULL(CU.ISDELETE,0) != 1
AND CU.CUSTNAME like CONCAT('%',#{custname},'%')
AND CONCAT( IFNULL(CU.MOBILE1,''), IFNULL(CU.MOBILE2,''), IFNULL(CU.MOBILE3,'') ) like CONCAT('%',#{mobile},'%')
AND CU.EMAIL like CONCAT('%',#{email},'%')
AND CUSTGUBUN = #{custgubun}
AND CUSTGRADE = #{custgrade}
AND ACTGRADE = #{actgrade}
AND CU.REGDATE >= #{regdatefr}
AND CU.REGDATE <= #{regdateto}
AND CU.INFOAGREE = #{infoagree}
AND CU.OWNER = #{owner}
AND CU.CLINO = #{clino}

